const categoryApi="https://opentdb.com/api_category.php";let categoryUserSelection,difficultyUserSelection,typeUserSelection;function categorySelection(){let e=document.getElementById("categorySelect");document.getElementsByTagName("option");categoryUserSelection=e.value;localStorage.setItem("category",categoryUserSelection);return categoryUserSelection}function difficultySelection(){let e=document.getElementById("difficultySelect");document.getElementsByTagName("option");difficultyUserSelection=e.value;localStorage.setItem("difficulty",difficultyUserSelection);return difficultyUserSelection}function typeSelection(){let e=document.getElementById("typeSelect");document.getElementsByTagName("option");typeUserSelection=e.value;localStorage.setItem("type",typeUserSelection);return typeUserSelection}fetch(categoryApi).then((e=>e.json())).then((e=>e.trivia_categories)).then((e=>{e.forEach((e=>{const t=document.createElement("option");t.value=e.id,t.innerText=e.name,categorySelect.appendChild(t)}))}));